                </View>

                <View style={styles.settingContent}>
                  <Text style={styles.settingTitle}>{option.title}</Text>
                  <Text style={styles.settingSubtitle}>{option.subtitle}</Text>
                </View>

                <Icons.ChevronRight size={20} color={Colors.textMuted} />
              </TouchableOpacity>
            );
          })}
