          <TouchableOpacity style={styles.supportItem}>
            <Icons.HelpCircle size={20} color={Colors.primary} />
            <Text style={styles.supportText}>Help & FAQ</Text>
            <Icons.ChevronRight size={20} color={Colors.textMuted} />
